// App.js

import React, { useContext } from 'react';
import { ThemeProvider } from 'styled-components';

import { ThemeContext } from './context/ThemeContext'; // <-- caminho corrigido
import GlobalStyle from './styles/GlobalStyle';        // <-- caminho corrigido
import { lightTheme, darkTheme } from './theme';       // <-- esse parece correto

function App() {
  const { theme } = useContext(ThemeContext);

  return (
    <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
      <GlobalStyle />
      <div>
        <h1>Meu Site</h1>
        <p>Bem-vindo ao nosso site!</p>
      </div>
    </ThemeProvider>
  );
}

export default App;
